  // Helper methods
  var validateAsList = function(obj) {
    return (obj === null || obj === undefined) ? [] : obj instanceof Array ? obj : [obj];
  }
  var hasIntersection = function(list1, list2, finder) {
    var fn = typeof finder === 'function' ? finder : hasValue;
    return list1.find(function(value) {return fn(list2, value)}) !== undefined;
  };
  var hasValue = function(list, value) { return list.indexOf(value) != -1 };

  // Data
  var me = [identity('username')];
  var requestedBy = validateAsList(values('Requested By', []));
  var requestedFor = validateAsList(values('Requested For', []));

  // Scheduler Admins
  return submission('createdBy') == identity('username')
  || hasIntersection(requestedBy, me)
  || hasIntersection(requestedFor, me);
})()

